'use strict';

function getNested(theObject, path) {
	try {
		const separator = '.';

		return path
			.replace('[', separator).replace(']', '')
			.split(separator)
			.reduce(
				(obj, property) => obj[property], theObject,
			);
	} catch (err) {
		return undefined;
	}
}

const replaceFromParams = (path, params, ctx) => {
